        version: "2.0",
        // 使用高德地图的最新版本
        plugins: ["AMap.Scale"] // 使用比例尺插件
      }).then(AMap => {
        map = new AMap.Map("container", {
          viewMode: "3D",
          // 3D 地图模式
          zoom: 11,
          // 初始地图缩放级别
          center: [116.397428, 39.90923] // 地图中心点（北京市中心）
        });
      }).catch(e => {
        console.log(e); // 打印错误信息
      });
    });
    onUnmounted(() => {
      if (map) {
        map.destroy(); // 销毁地图实例
      }
    });
    const __returned__ = {
      get map() {
        return map;
      },
      set map(v) {
        map = v;
      },
      onMounted,
      onUnmounted,
      get AMapLoader() {
        return AMapLoader;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
